function App() {
const [myColor, setColor]= useState(colors[rand()]);
const changeColor = ()=> {
   setColor('red')
}

const cubes = [1, 2, 3, 4, 5, 6, 7, 8, 9];



       return (
    <div className="App">
      <div className="container">  
        {cubes.map(x => <Cube 
      key={x}
      backgroundColor= {myColor}
      onChangeCol = {changeColor()}
       />)}
     </div> 
     </div>


  );
}

export default App;
