import { useState } from "react";
import "./App.css";
import Cube from "./Cube";





const colors = ['white', 'red', 'blue', 'green', 'yellow', 'orange']
const rand =()=> Math.floor(Math.random()*6);




function App() {
const [myColor, setColor]= useState('');
const changeColor = ()=> {
  const ind =  colors.findIndex(myColor);
   setColor(colors[ind+1])}
const cubes = [];

     cubes.array(9).fill(<Cube 
      key='ind'
      backgroundColor= {colors[rand()]}
      onClick = {()=> changeColor()}
       />)


       return (
    <div className="App">
      <div className="container">  
        {cubes}
     </div> 
     </div>


  );
}

export default App;
