function App() {
const [myColor, setColor]= useState('');



       return (
    <div className="App">
      <div className="container">  
        {colors.map(x => <Cube 
      key={x}
      backgroundColor= {myColor}
      onClick={setColor(colors[x])}
       />)}
     </div> 
     </div>


  );
}

export default App;
